import React , {useState,useEffect,useCallback} from 'react';
import MoviesList from './components/MoviesList';
import './App.css';
import AddMovie from './components/AddMovie';

function App() {
  const [movies,setMovies]=useState([]);
  const [isLoading,setIsLoading]=useState(false);
  const [error,setError] = useState(null);
const fetchMoviesHandler= useCallback(async()=>{
    setIsLoading(true);
    setError(null);
    try{
      const response = await fetch('https://swapi.dev/api/films');
      const data=await response.json();
      const requiredData=data.results.map(movie=>{
        return{
          id: movie.episode_id,
          title: movie.title,
          openingText: movie.opening_crawl,
          releaseDate: movie.release_date,
        }
      })
      setMovies(requiredData);
    }
    catch(error){
   setError(error.message);
    }

    setIsLoading(false);
  },[])

  useEffect(()=>{
    fetchMoviesHandler();
  },[fetchMoviesHandler])

  let content=<p>No Movies Found</p>;
  if(isLoading) content=<p>Loading...</p>
  if(!isLoading && movies.length>=1) content= <MoviesList movies={movies} />;
  if(error) content=<p style={{ color: 'red',  fontWeight: 'bold' }}>something went wrong..try agin after 1 minute </p>;

  function addMovieHandler(movie){
  console.log(movie)
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        <AddMovie onAddMovie={addMovieHandler}/>
      </section>
      <section>
       {content}
      </section>
    </React.Fragment>
  );
}

export default App;
